      <node TEXT="Cache - Control" ID="oDnpVKSesx" _mubu_text="%3Cspan%3ECache%20-%20Control%3C/span%3E" STYLE="fork">
        <node TEXT="max-age" ID="FzG2e8Tr5T" _mubu_text="%3Cspan%3Emax-age%3C/span%3E" STYLE="fork">
          <node TEXT="在响应header中出现，代表缓存过期的相对时间，单位为秒数。" ID="ZMf1sCcwBO" _mubu_text="%3Cspan%3E%E5%9C%A8%E5%93%8D%E5%BA%94header%E4%B8%AD%E5%87%BA%E7%8E%B0%EF%BC%8C%E4%BB%A3%E8%A1%A8%E7%BC%93%E5%AD%98%E8%BF%87%E6%9C%9F%E7%9A%84%3C/span%3E%3Cspan%20class=%22bold%22%3E%E7%9B%B8%E5%AF%B9%E6%97%B6%E9%97%B4%3C/span%3E%3Cspan%3E%EF%BC%8C%E5%8D%95%E4%BD%8D%E4%B8%BA%E7%A7%92%E6%95%B0%E3%80%82%3C/span%3E" STYLE="fork"/>
          <node TEXT="在请求header中出现，代表告知服务器客户端希望接收一个存在时间（age）不大于多少秒的资源。" ID="mZNshFoTu7" _mubu_text="%3Cspan%3E%E5%9C%A8%E8%AF%B7%E6%B1%82header%E4%B8%AD%E5%87%BA%E7%8E%B0%EF%BC%8C%E4%BB%A3%E8%A1%A8%E5%91%8A%E7%9F%A5%E6%9C%8D%E5%8A%A1%E5%99%A8%E5%AE%A2%E6%88%B7%E7%AB%AF%E5%B8%8C%E6%9C%9B%E6%8E%A5%E6%94%B6%E4%B8%80%E4%B8%AA%E5%AD%98%E5%9C%A8%E6%97%B6%E9%97%B4%EF%BC%88age%EF%BC%89%E4%B8%8D%E5%A4%A7%E4%BA%8E%E5%A4%9A%E5%B0%91%E7%A7%92%E7%9A%84%E8%B5%84%E6%BA%90%E3%80%82%3C/span%3E" STYLE="fork"/>
        </node>
        <node TEXT="s-maxage" ID="VmnHLl9fZp" _mubu_text="%3Cspan%3Es-maxage%3C/span%3E" STYLE="fork">
          <node TEXT="只适用于公共缓存服务器，比如资源从源服务器发出后又被中间的代理服务器接收并缓存。" ID="GeoF7UkhpA" _mubu_text="%3Cspan%3E%E5%8F%AA%E9%80%82%E7%94%A8%E4%BA%8E%E5%85%AC%E5%85%B1%E7%BC%93%E5%AD%98%E6%9C%8D%E5%8A%A1%E5%99%A8%EF%BC%8C%E6%AF%94%E5%A6%82%E8%B5%84%E6%BA%90%E4%BB%8E%E6%BA%90%E6%9C%8D%E5%8A%A1%E5%99%A8%E5%8F%91%E5%87%BA%E5%90%8E%E5%8F%88%E8%A2%AB%E4%B8%AD%E9%97%B4%E7%9A%84%E4%BB%A3%E7%90%86%E6%9C%8D%E5%8A%A1%E5%99%A8%E6%8E%A5%E6%94%B6%E5%B9%B6%E7%BC%93%E5%AD%98%E3%80%82%3C/span%3E" STYLE="fork"/>
          <node TEXT="使用 s-maxage 指令后，公共缓存服务器将直接忽略 Expires 和 max-age 指令的值。" ID="ZGPKhauGlo" _mubu_text="%3Cspan%20class=%22bold%22%3E%E4%BD%BF%E7%94%A8%20s-maxage%20%E6%8C%87%E4%BB%A4%E5%90%8E%EF%BC%8C%E5%85%AC%E5%85%B1%E7%BC%93%E5%AD%98%E6%9C%8D%E5%8A%A1%E5%99%A8%E5%B0%86%E7%9B%B4%E6%8E%A5%E5%BF%BD%E7%95%A5%20Expires%20%E5%92%8C%20max-age%20%E6%8C%87%E4%BB%A4%E7%9A%84%E5%80%BC%E3%80%82%3C/span%3E" STYLE="fork"/>
        </node>
        <node TEXT="public/private" ID="2K56en6yQO" _mubu_text="%3Cspan%3Epublic/private%3C/span%3E" STYLE="fork">
          <node TEXT="public 指令表示该资源可以被任何节点缓存（包括客户端和代理服务器），与其行为相反的 private 指令表示该资源只提供给客户端缓存，代理服务器不会进行缓存" ID="sKxEmY2eXg" _mubu_text="%3Cspan%3Epublic%20%E6%8C%87%E4%BB%A4%E8%A1%A8%E7%A4%BA%E8%AF%A5%E8%B5%84%E6%BA%90%E5%8F%AF%E4%BB%A5%E8%A2%AB%E4%BB%BB%E4%BD%95%E8%8A%82%E7%82%B9%E7%BC%93%E5%AD%98%EF%BC%88%E5%8C%85%E6%8B%AC%E5%AE%A2%E6%88%B7%E7%AB%AF%E5%92%8C%E4%BB%A3%E7%90%86%E6%9C%8D%E5%8A%A1%E5%99%A8%EF%BC%89%EF%BC%8C%E4%B8%8E%E5%85%B6%E8%A1%8C%E4%B8%BA%E7%9B%B8%E5%8F%8D%E7%9A%84%20private%20%E6%8C%87%E4%BB%A4%E8%A1%A8%E7%A4%BA%E8%AF%A5%E8%B5%84%E6%BA%90%E5%8F%AA%E6%8F%90%E4%BE%9B%E7%BB%99%E5%AE%A2%E6%88%B7%E7%AB%AF%E7%BC%93%E5%AD%98%EF%BC%8C%E4%BB%A3%E7%90%86%E6%9C%8D%E5%8A%A1%E5%99%A8%E4%B8%8D%E4%BC%9A%E8%BF%9B%E8%A1%8C%E7%BC%93%E5%AD%98%3C/span%3E" STYLE="fork"/>
          <node TEXT="当设置了 private 指令后 s-maxage 指令将被忽略。" ID="hkqhIGduPf" _mubu_text="%3Cspan%20class=%22bold%22%3E%E5%BD%93%E8%AE%BE%E7%BD%AE%E4%BA%86%20private%20%E6%8C%87%E4%BB%A4%E5%90%8E%20s-maxage%20%E6%8C%87%E4%BB%A4%E5%B0%86%E8%A2%AB%E5%BF%BD%E7%95%A5%E3%80%82%3C/span%3E" STYLE="fork"/>
        </node>
        <node TEXT="no-cache/no-store" ID="D4LtZZn9Ut" _mubu_text="%3Cspan%3Eno-cache/no-store%3C/span%3E" STYLE="fork">
          <node TEXT="no-cache 在请求首部中被使用时，表示告知（代理）服务器不直接使用缓存，要求向源服务器发起请求，而当在响应首部中被返回时，表示客户端可以缓存资源，但每次使用缓存资源前都必须先向服务器确认其有效性，这对每次访问都需要确认身份的应用来说很有用" ID="sqAZfWiVv4" _mubu_text="%3Cspan%3Eno-cache%20%E5%9C%A8%E8%AF%B7%E6%B1%82%E9%A6%96%E9%83%A8%E4%B8%AD%E8%A2%AB%E4%BD%BF%E7%94%A8%E6%97%B6%EF%BC%8C%E8%A1%A8%E7%A4%BA%E5%91%8A%E7%9F%A5%EF%BC%88%E4%BB%A3%E7%90%86%EF%BC%89%E6%9C%8D%E5%8A%A1%E5%99%A8%E4%B8%8D%E7%9B%B4%E6%8E%A5%E4%BD%BF%E7%94%A8%E7%BC%93%E5%AD%98%EF%BC%8C%E8%A6%81%E6%B1%82%E5%90%91%E6%BA%90%E6%9C%8D%E5%8A%A1%E5%99%A8%E5%8F%91%E8%B5%B7%E8%AF%B7%E6%B1%82%EF%BC%8C%E8%80%8C%E5%BD%93%E5%9C%A8%E5%93%8D%E5%BA%94%E9%A6%96%E9%83%A8%E4%B8%AD%E8%A2%AB%E8%BF%94%E5%9B%9E%E6%97%B6%EF%BC%8C%E8%A1%A8%E7%A4%BA%E5%AE%A2%E6%88%B7%E7%AB%AF%E5%8F%AF%E4%BB%A5%E7%BC%93%E5%AD%98%E8%B5%84%E6%BA%90%EF%BC%8C%E4%BD%86%E6%AF%8F%E6%AC%A1%E4%BD%BF%E7%94%A8%E7%BC%93%E5%AD%98%E8%B5%84%E6%BA%90%E5%89%8D%E9%83%BD%3C/span%3E%3Cspan%20class=%22bold%22%3E%E5%BF%85%E9%A1%BB%3C/span%3E%3Cspan%3E%E5%85%88%E5%90%91%E6%9C%8D%E5%8A%A1%E5%99%A8%E7%A1%AE%E8%AE%A4%E5%85%B6%E6%9C%89%E6%95%88%E6%80%A7%EF%BC%8C%E8%BF%99%E5%AF%B9%E6%AF%8F%E6%AC%A1%E8%AE%BF%E9%97%AE%E9%83%BD%E9%9C%80%E8%A6%81%E7%A1%AE%E8%AE%A4%E8%BA%AB%E4%BB%BD%E7%9A%84%E5%BA%94%E7%94%A8%E6%9D%A5%E8%AF%B4%E5%BE%88%E6%9C%89%E7%94%A8%3C/span%3E" STYLE="fork"/>
          <node TEXT="no store 是真正的不进行任何缓存。" ID="Pezsh5iUZx" _mubu_text="%3Cspan%3Eno%20store%20%E6%98%AF%E7%9C%9F%E6%AD%A3%E7%9A%84%E4%B8%8D%E8%BF%9B%E8%A1%8C%E4%BB%BB%E4%BD%95%E7%BC%93%E5%AD%98%E3%80%82%3C/span%3E" STYLE="fork"/>
        </node>
      </node>
    </node>
    <node TEXT="协商缓存" ID="sNWXod0M4m" _mubu_text="%3Cspan%3E%E5%8D%8F%E5%95%86%E7%BC%93%E5%AD%98%3C/span%3E" STYLE="bubble" POSITION="right">
      <node TEXT="Last-Modified" ID="8iDxXyMjaC" _mubu_text="%3Cspan%3ELast-Modified%3C/span%3E" STYLE="fork">
        <node TEXT="代表资源的最后修改时间，其属于响应首部字段。" ID="nX7eAbTEAs" _mubu_text="%3Cspan%3E%E4%BB%A3%E8%A1%A8%E8%B5%84%E6%BA%90%E7%9A%84%E6%9C%80%E5%90%8E%E4%BF%AE%E6%94%B9%E6%97%B6%E9%97%B4%EF%BC%8C%E5%85%B6%E5%B1%9E%E4%BA%8E%3C/span%3E%3Cspan%20class=%22bold%22%3E%E5%93%8D%E5%BA%94%E9%A6%96%E9%83%A8%E5%AD%97%E6%AE%B5%E3%80%82%3C/span%3E" STYLE="fork"/>
        <node TEXT="当浏览器第一次接收到服务器返回资源的 Last-Modified 值后，其会把这个值存储起来，并再下次访问该资源时通过携带 If-Modified-Since 请求首部发送给服务器验证该资源有没有过期。" ID="VxicvhWnO2" _mubu_text="%3Cspan%3E%E5%BD%93%E6%B5%8F%E8%A7%88%E5%99%A8%E7%AC%AC%E4%B8%80%E6%AC%A1%E6%8E%A5%E6%94%B6%E5%88%B0%E6%9C%8D%E5%8A%A1%E5%99%A8%E8%BF%94%E5%9B%9E%E8%B5%84%E6%BA%90%E7%9A%84%20Last-Modified%20%E5%80%BC%E5%90%8E%EF%BC%8C%E5%85%B6%E4%BC%9A%E6%8A%8A%E8%BF%99%E4%B8%AA%E5%80%BC%E5%AD%98%E5%82%A8%E8%B5%B7%E6%9D%A5%EF%BC%8C%E5%B9%B6%E5%86%8D%E4%B8%8B%E6%AC%A1%E8%AE%BF%E9%97%AE%E8%AF%A5%E8%B5%84%E6%BA%90%E6%97%B6%E9%80%9A%E8%BF%87%E6%90%BA%E5%B8%A6%20If-Modified-Since%20%E8%AF%B7%E6%B1%82%E9%A6%96%E9%83%A8%E5%8F%91%E9%80%81%E7%BB%99%E6%9C%8D%E5%8A%A1%E5%99%A8%E9%AA%8C%E8%AF%81%E8%AF%A5%E8%B5%84%E6%BA%90%E6%9C%89%E6%B2%A1%E6%9C%89%E8%BF%87%E6%9C%9F%E3%80%82%3C/span%3E" STYLE="fork"/>
      </node>
      <node TEXT="If-Modified-Since" ID="53pqpbQo9s" _mubu_text="%3Cspan%3EIf-Modified-Since%3C/span%3E" STYLE="fork"/>
      <node TEXT="ETag" ID="06BJBqUqDo" _mubu_text="%3Cspan%3EETag%3C/span%3E" STYLE="fork">
        <node TEXT="首部字段用于代表资源的唯一性标识，服务器会按照指定的规则生成资源的标识，其属于响应首部字段" ID="JypUECmGvy" _mubu_text="%3Cspan%3E%E9%A6%96%E9%83%A8%E5%AD%97%E6%AE%B5%E7%94%A8%E4%BA%8E%E4%BB%A3%E8%A1%A8%E8%B5%84%E6%BA%90%E7%9A%84%E5%94%AF%E4%B8%80%E6%80%A7%E6%A0%87%E8%AF%86%EF%BC%8C%E6%9C%8D%E5%8A%A1%E5%99%A8%E4%BC%9A%E6%8C%89%E7%85%A7%E6%8C%87%E5%AE%9A%E7%9A%84%E8%A7%84%E5%88%99%E7%94%9F%E6%88%90%E8%B5%84%E6%BA%90%E7%9A%84%E6%A0%87%E8%AF%86%EF%BC%8C%E5%85%B6%E5%B1%9E%E4%BA%8E%3C/span%3E%3Cspan%20class=%22bold%22%3E%E5%93%8D%E5%BA%94%E9%A6%96%E9%83%A8%E5%AD%97%E6%AE%B5%3C/span%3E" STYLE="fork"/>
        <node TEXT="当浏览器第一次接收到服务器返回资源的 Etag 值后，其会把这个值存储起来，并在下次访问该资源时通过携带 If-None-Match 请求首部发送给服务器验证该资源有没有过期。" ID="ZbDszAOhbK" _mubu_text="%3Cspan%3E%E5%BD%93%E6%B5%8F%E8%A7%88%E5%99%A8%E7%AC%AC%E4%B8%80%E6%AC%A1%E6%8E%A5%E6%94%B6%E5%88%B0%E6%9C%8D%E5%8A%A1%E5%99%A8%E8%BF%94%E5%9B%9E%E8%B5%84%E6%BA%90%E7%9A%84%20Etag%20%E5%80%BC%E5%90%8E%EF%BC%8C%E5%85%B6%E4%BC%9A%E6%8A%8A%E8%BF%99%E4%B8%AA%E5%80%BC%E5%AD%98%E5%82%A8%E8%B5%B7%E6%9D%A5%EF%BC%8C%E5%B9%B6%E5%9C%A8%E4%B8%8B%E6%AC%A1%E8%AE%BF%E9%97%AE%E8%AF%A5%E8%B5%84%E6%BA%90%E6%97%B6%E9%80%9A%E8%BF%87%E6%90%BA%E5%B8%A6%20If-None-Match%20%E8%AF%B7%E6%B1%82%E9%A6%96%E9%83%A8%E5%8F%91%E9%80%81%E7%BB%99%E6%9C%8D%E5%8A%A1%E5%99%A8%E9%AA%8C%E8%AF%81%E8%AF%A5%E8%B5%84%E6%BA%90%E6%9C%89%E6%B2%A1%E6%9C%89%E8%BF%87%E6%9C%9F%E3%80%82%3C/span%3E" STYLE="fork"/>
        <node TEXT="eTag 的优先级要高于 last-modified，当两者同时出现时，只有 eTag 会生效。" ID="EaHfd3WOWE" _mubu_text="%3Cspan%3EeTag%20%E7%9A%84%E4%BC%98%E5%85%88%E7%BA%A7%E8%A6%81%E9%AB%98%E4%BA%8E%20last-modified%EF%BC%8C%E5%BD%93%E4%B8%A4%E8%80%85%E5%90%8C%E6%97%B6%E5%87%BA%E7%8E%B0%E6%97%B6%EF%BC%8C%E5%8F%AA%E6%9C%89%20eTag%20%E4%BC%9A%E7%94%9F%E6%95%88%E3%80%82%3C/span%3E" STYLE="fork"/>
      </node>
      <node TEXT="If-None-Match" ID="iSw3T6RRTV" _mubu_text="%3Cspan%3EIf-None-Match%3C/span%3E" STYLE="fork"/>
    </node>
